sys_script.script:
try {
    // Get user ID and status from the user session record
    var userId = current.getValue('user');
    var sessionType = current.getValue('session_type');
    var workLocation = current.getValue('work_location');

    // Map session type to user status
    var status = 'logout';
    if (sessionType === 'login') {
        status = 'online';
    } else if (sessionType === 'break_start') {
        status = 'break';
    } else if (sessionType === 'break_end') {
        status = 'online';
    } else if (sessionType === 'logout') {
        status = 'logout';
    }

    // Update user status fields
    var userGr = new GlideRecord('sys_user');
    if (userGr.get(userId)) {
        var now = new GlideDateTime().getDisplayValue();

        userGr.setValue('x_1599224_smart_of_login_status', status);

        if (workLocation) {
            userGr.setValue('x_1599224_smart_of_work_location', workLocation);
        }

        // Update first login and last logout times
        var today = new GlideDateTime().getDisplayValueInternal().substr(0, 10);

        if (status === 'online') {
            // Check if this is first login of the day
            if (!userGr.getValue('x_1599224_smart_of_first_login') || 
                !userGr.getValue('x_1599224_smart_of_first_login').startsWith(today)) {
                userGr.setValue('x_1599224_smart_of_first_login', now);
            }
        } else if (status === 'logout') {
            userGr.setValue('x_1599224_smart_of_last_logout', now);
        } else if (status === 'break') {
            // Increment break count
            var currentBreaks = parseInt(userGr.getValue('x_1599224_smart_of_no_of_breaks') || '0');
            userGr.setValue('x_1599224_smart_of_no_of_breaks', currentBreaks + 1);
        }

        userGr.update();
    }
} catch (error) {
    gs.error('Error updating user status: ' + error.message);
}
